import { useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { getPostCode } from "../reducers/postsinfo";
import { useRouter } from "next/router";

const useStyles = makeStyles(theme => ({
  root: {
    minHeight: "calc(100vh)",
    backgroundColor: "rgb(240 240 240)"
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  console.log(props.post);
  useEffect(() => {
    props.getPostCode(route.query.code);
  }, []);
  const MyPage = (
    <div className={classes.root}>
      <ActionMyBar homeEnable={false} title={props.post.code || "포스트"} />
      <p>{props.post.title}</p>
      <text>{props.post.content}</text>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

const mapStateToProps = state => {
  return { post: state.postsinfo.selectedPost };
};
const mapDispatchToProps = dispatch =>
  bindActionCreators({ getPostCode }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Index);
