import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import { useRouter } from "next/router";
import { Card, Button, Collapse, CardHeader } from "@material-ui/core";
import { KeyboardArrowUp as ArrowUp } from "@material-ui/icons";
import { BASE_COLOR } from "../styles/typography";
import { useState } from "react";

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: BASE_COLOR,
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  content: {
    padding: "0px 0px 0px",
    textAlign: "center"
  },
  card: {
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    flexDirection: "column",
    backgroundColor: "rgb(240 240 240)",
    padding: "20px 20px 10px"
  },
  item: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    flexDirection: "column",
    marginBottom: "10px",
    padding: "5px 10px",
    minWidth: "350px"
  },
  itemHeader: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "10px",
    padding: "5px 10px",
    minWidth: "350px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  button: {
    margin: "5px"
  },
  appName: {
    color: "white",
    textShadow:
      "-1px -1px 0 blue, 1px -1px 0 blue, -1px 1px 0 blue, 1px 1px 0 blue"
  },
  platformName: {
    fontWeight: "bold"
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  const [naverChecked, setNaverChecked] = useState(false);
  const MyPage = (
    <div className={classes.root}>
      <div className={classes.content}>
        <h1 className={classes.appName}>블로그 셋팅</h1>
        <Card className={classes.card}>
          <Card className={classes.item}>
            <CardHeader className={classes.itemHeader}>
              <span className={classes.platformName}>네이버</span>
              <Button endIcon={<ArrowUp />}>등록</Button>
            </CardHeader>
            <Collapse in={naverChecked}></Collapse>
          </Card>
          <Card className={classes.item}>
            <span className={classes.platformName}>셋팅</span>
            <Button endIcon={<ArrowUp />}>등록</Button>
          </Card>
        </Card>
        <div className={classes.actions}>
          <Button
            className={classes.button}
            variant="contained"
            color="secondary"
            onClick={() => {
              const result = window.confirm(
                "변경된 내용을 저장하지 않고 취소하겠습니까?"
              );
              if (result) route.push("/login");
            }}
          >
            취소
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            onClick={() => {
              const result = window.confirm("변경된 내용을 저장하겠습니까?");
              if (result) route.push("/login");
            }}
          >
            저장
          </Button>
        </div>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

export default Index;
