import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Tabss from "../tab/Tabs";
import styled from "styled-components";

import { pTr, BASE_COLOR } from "../../styles/typography";

const useStyles = makeStyles({
  root: {
    flexGrow: 1
  },
  button: {
    boxShadow: "0 0 0 0",
    borderRadius: 0,
    borderWidth: "1px",
    backgroundColor: "rgb(255 255 255)",
    color: "rgb(45 47 49)",
    borderColor: "rgb(217 217 217)",
    font: `350 ${pTr(14)} NanumSquare`,
    borderRadius: "4px",
    margin: pTr(5),
    minWidth: pTr(76),
    minHeight: pTr(38),
    letterSpacing: 0,
    "&:hover": {
      backgroundColor: BASE_COLOR,
      color: "white"
    }
  },
  button2: {
    boxShadow: "0 0 0 0",
    borderRadius: 0,
    padding: 0,
    borderWidth: "1px",
    backgroundColor: "rgb(255 255 255)",
    color: "rgb(45 47 49)",
    borderColor: "rgb(217 217 217)",
    font: `350 ${pTr(14)} NanumSquare`,
    borderRadius: "4px",
    margin: pTr(5),
    minWidth: pTr(162),
    minHeight: pTr(38),
    letterSpacing: 0,
    "&:hover": {
      backgroundColor: BASE_COLOR,
      color: "white"
    }
  }
});

const GridGutterA = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: center;
  }
`;
const GridGutterB = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;
const GridGutterC = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;

const ButtonGridA = props => {
  return (
    <GridGutterA>
      {props.data.map(item => {
        return (
          <>
            <p>code : {item.code}</p>
            <p>title : {item.title}</p>
          </>
        );
      })}
    </GridGutterA>
  );
};

const ButtonGridB = props => {
  return <GridGutterB></GridGutterB>;
};

const ButtonGridC = props => {
  return <GridGutterC></GridGutterC>;
};

function HomeArea2(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Tabss
        titles={["미정포스트", "티스토리", "네이버"]}
        contents={[
          <ButtonGridA data={props.posts} />,
          <ButtonGridB data={[]} />,
          <ButtonGridC data={[]} />
        ]}
      />
    </div>
  );
}

export default HomeArea2;
