import React, { useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import { Divider, Typography, Box, Tab, Tabs } from "@material-ui/core";
import { connect } from "react-redux";

import styled from "styled-components";
import { pTr, BASE_COLOR } from "../../styles/typography";

const AntTabs = withStyles(theme => ({
  root: {
    flexGrow: 1,
    borderBottom: `1px solid #e8e8e8`,
    backgroundColor: theme.palette.background.paper,
    height: pTr(36)
  },
  indicator: {
    backgroundColor: BASE_COLOR,
    color: BASE_COLOR,
    height: pTr(3)
  }
}))(Tabss);

const AntTab = withStyles(theme => ({
  root: {
    textTransform: "none",
    fontSize: pTr(14),
    color: "rgb(93 93 93)",
    "&:focus": {
      color: BASE_COLOR
    }
  }
}))(props => <Tab disableRipple {...props} />);

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  padding: {
    padding: pTr(20)
  },
  box: {
    backgroundColor: "rgb(246 246 246)"
  },
  errorRoot: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  }
}));

function TabPanel(props) {
  const { children, value, index, boxColor, ...other } = props;

  return (
    <div
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`nav-tabpanel-${index}`}
      aria-labelledby={`nav-tab-${index}`}
      {...other}
    >
      <Box
        style={{
          backgroundColor: boxColor ? boxColor : "rgb(246 246 246)",
          width: "100%",
          margin: 0,
          padding: 0
        }}
        p={pTr(15)}
      >
        {children}
      </Box>
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
};

function Tabss({
  boxColor = "",
  errMsg = "",
  titles = [""],
  contents = [{}],
  selected_tab = ""
}) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const handleChange = useCallback((event, newValue) => {
    setValue(newValue);
  }, []);

  let contentCountMissMatch = false;

  if (contentCountMissMatch) {
    return <div className={classes.errorRoot}></div>;
  }

  return (
    <div className={classes.root}>
      <div className={classes.box}>
        <Divider />
        <div>
          <AntTabs
            value={value}
            onChange={handleChange}
            // textColor={BASE_COLOR}
            aria-label="ant"
            variant="fullWidth"
          >
            {titles.map(title => (
              <AntTab label={title} />
            ))}
          </AntTabs>
          {contents.map((content, index) => (
            <TabPanel
              key={titles[index] + "-" + index}
              value={value}
              index={0}
              boxColor={boxColor}
            >
              {content}
            </TabPanel>
          ))}
        </div>
      </div>
    </div>
  );
}
function mapStateToProps(state) {
  return {
    selected_tab:
      state.searchinfo.selected_tab == undefined
        ? "doctor"
        : state.searchinfo.selected_tab
  };
}

export default connect(mapStateToProps)(Tabss);
