import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import axios from "axios";
// import { useDispatch } from "react-redux";
import { BASE_URL, API_TIME_OUT } from "../contants/constants";

function useTistoryLogin(access_token = "") {
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState();
  //   const dispatch = useDispatch();

  useEffect(() => {
    if (access_token) {
      setLoading(true);
      axios
        .get(`http://${BASE_URL}/tistory/blogs`, {
          params: { access_token: access_token },
          timeout: API_TIME_OUT / 2 // 15초 이내에 응답이 오지 않으면 에러로 간주
        })
        .then(res => {
          setData(res.data);
          setLoading(false);
          return res;
          // dispatch({ type: GET_POSTS_LIST, payload: { data: res.data } });
        })
        .catch(error => {
          setError(error);
          setLoading(false);
          return error;
        });
    }
  }, [access_token]);

  return {
    data,
    loading,
    error
  };
}

useTistoryLogin.propTypes = {};

export default useTistoryLogin;
