import { useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { getPostCode } from "../reducers/postsinfo";
import { useRouter } from "next/router";
import { TextField, FormControl, Card } from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  root: {},
  content: {
    padding: "64px 20px 20px"
  },
  form: {},
  card: {
    backgroundColor: "rgb(240 240 240)",
    padding: "20px"
  },
  textField: {
    margin: "10px 10px 20px"
  },
  action: {}
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  console.log(props.post);
  useEffect(() => {
    props.getPostCode(route.query.code);
  }, []);
  const MyPage = (
    <div className={classes.root}>
      <ActionMyBar homeEnable={false} title={props.post.code || "포스트"} />
      <div className={classes.content}>
        <div></div>
        <Card className={classes.card}>
          <FormControl fullWidth variant="outlined">
            <TextField
              className={classes.textField}
              multiline
              fullWidth
              label={"제목"}
              defaultValue={props.post.title}
            />
            <TextField
              className={classes.textField}
              multiline
              fullWidth
              label={"내용"}
              defaultValue={props.post.context}
            />
          </FormControl>
        </Card>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

const mapStateToProps = state => {
  return { post: state.postsinfo.selectedPost };
};
const mapDispatchToProps = dispatch =>
  bindActionCreators({ getPostCode }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Index);
