import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card } from "@material-ui/core";
import Tabss from "../tab/Tabs";
import styled from "styled-components";

import { pTr, BASE_COLOR } from "../../styles/typography";
import { useRouter } from "next/router";

const useStyles = makeStyles({
  root: {
    flexGrow: 1
  },
  item: {
    marginBottom: "20px",
    paddingLeft: "10px",
    textAlign: "left",
    cursor: "pointer"
  }
});

const GridGutterA = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: center;
  }
`;
const GridGutterB = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;
const GridGutterC = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;

const ButtonGridA = props => {
  const route = useRouter();
  return (
    <GridGutterA>
      {props.data.map((item = { code: "", title: "" }) => {
        return (
          <Card
            key={"title-" + item.code}
            className={props.classes.item}
            onClick={() => {
              route.push(`/post/${item.code}`);
            }}
          >
            <p>{item.code}</p>
            <p>{item.title}</p>
          </Card>
        );
      })}
    </GridGutterA>
  );
};

const ButtonGridB = props => {
  return <GridGutterB></GridGutterB>;
};

const ButtonGridC = props => {
  return <GridGutterC></GridGutterC>;
};

function HomeArea2(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Tabss
        titles={["미정포스트", "티스토리", "네이버"]}
        contents={[
          <ButtonGridA data={props.posts} classes={classes} />,
          <ButtonGridB data={[]} classes={classes} />,
          <ButtonGridC data={[]} classes={classes} />
        ]}
      />
    </div>
  );
}

export default HomeArea2;
