import { useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { getPostCode } from "../reducers/postsinfo";
import { useRouter } from "next/router";
import { TextField, FormControl, Card, Button } from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  root: {},
  content: {
    padding: "64px 0px 0px"
  },
  card: {
    backgroundColor: "rgb(240 240 240)",
    padding: "20px",
    margin: "0px 20px 20px"
  },
  textField: {
    margin: "10px 10px 20px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  button: {
    margin: "5px"
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  console.log(props.post);
  useEffect(() => {
    props.getPostCode(route.query.code);
  }, []);
  const MyPage = (
    <div className={classes.root}>
      <ActionMyBar homeEnable={false} title={props.post.code || "포스트"} />
      <div className={classes.content}>
        <div className={classes.actions}>
          <Button
            className={classes.button}
            variant="contained"
            color="secondary"
          >
            삭제
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
          >
            저장
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="default"
          >
            포스팅
          </Button>
        </div>
        <Card className={classes.card}>
          <FormControl fullWidth variant="outlined">
            <TextField
              className={classes.textField}
              multiline
              fullWidth
              label={"제목"}
              defaultValue={props.post.title}
            />
            <TextField
              className={classes.textField}
              multiline
              fullWidth
              label={"내용"}
              defaultValue={props.post.context}
            />
          </FormControl>
        </Card>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

const mapStateToProps = state => {
  return { post: state.postsinfo.selectedPost };
};
const mapDispatchToProps = dispatch =>
  bindActionCreators({ getPostCode }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Index);
