import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card } from "@material-ui/core";
import Tabss from "../tab/Tabs";
import styled from "styled-components";

import { pTr, BASE_COLOR } from "../../styles/typography";

const useStyles = makeStyles({
  root: {
    flexGrow: 1
  },
  item: {
    marginBottom: "20px"
  }
});

const GridGutterA = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: center;
  }
`;
const GridGutterB = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;
const GridGutterC = styled.div`
  && {
    padding: ${pTr(15)};
    text-align: left;
  }
`;

const ButtonGridA = props => {
  return (
    <GridGutterA>
      {props.data.map(item => {
        return (
          <Card className={props.classes}>
            <p>code : {item.code}</p>
            <p>title : {item.title}</p>
          </Card>
        );
      })}
    </GridGutterA>
  );
};

const ButtonGridB = props => {
  return <GridGutterB></GridGutterB>;
};

const ButtonGridC = props => {
  return <GridGutterC></GridGutterC>;
};

function HomeArea2(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Tabss
        titles={["미정포스트", "티스토리", "네이버"]}
        contents={[
          <ButtonGridA data={props.posts} classes={classes} />,
          <ButtonGridB data={[]} classes={classes} />,
          <ButtonGridC data={[]} classes={classes} />
        ]}
      />
    </div>
  );
}

export default HomeArea2;
