import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { useRouter } from "next/router";
import {
  TextField,
  FormControl,
  Card,
  Button,
  CircularProgress
} from "@material-ui/core";
import useDataOne from "../useActions/useDataOne";

const useStyles = makeStyles(theme => ({
  root: {},
  content: {
    padding: "64px 0px 0px"
  },
  card: {
    backgroundColor: "rgb(240 240 240)",
    padding: "20px",
    margin: "0px 20px 20px"
  },
  textField: {
    margin: "10px 10px 20px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  button: {
    margin: "5px"
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  const { data: post, loading } = useDataOne("posts", route.query.code);
  console.log(post);
  const MyPage = (
    <div className={classes.root}>
      <ActionMyBar homeEnable={false} title={post.code || "포스트"} />
      <div className={classes.content}>
        <div className={classes.actions}>
          <Button
            className={classes.button}
            variant="contained"
            color="secondary"
          >
            삭제
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
          >
            저장
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="default"
          >
            포스팅
          </Button>
        </div>

        <Card className={classes.card}>
          {loading ? (
            <CircularProgress />
          ) : (
            <FormControl fullWidth variant="outlined">
              <TextField
                className={classes.textField}
                multiline
                fullWidth
                label={"제목"}
                defaultValue={post.title}
              />
              <TextField
                className={classes.textField}
                multiline
                fullWidth
                label={"내용"}
                defaultValue={post.context}
              />
            </FormControl>
          )}
        </Card>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

export default Index;
