import React, { useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/styles";
import { getPostsList } from "../reducers/postsinfo";

import { pTr, BASE_COLOR } from "../styles/typography";

import HomeArea1 from "../components/area/HomeArea1";
import HomeArea2 from "../components/area/HomeArea2";
// import AppBar from "../components/component/AppBar";

const useStyles = makeStyles(theme => ({
  root: {
    minHeight: "calc(100vh - 89px)"
  }
}));

const HomePage = props => {
  const classes = useStyles();
  useEffect(() => {
    props.getPostsList();
  }, []);
  return (
    <div className={classes.root}>
      <HomeArea1 {...props} />
      <HomeArea2 {...props} />
    </div>
  );
};

const mapStateToProps = state => {
  return { posts: state.postsinfo.posts };
};
const mapDispatchToProps = dispatch =>
  bindActionCreators({ getPostsList }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
