import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import { useRouter } from "next/router";
import {
  Card,
  Button,
  Collapse,
  CardActions,
  TextField
} from "@material-ui/core";
import {
  KeyboardArrowUp as ArrowUp,
  KeyboardArrowDown as ArrowDown
} from "@material-ui/icons";
import { BASE_COLOR } from "../styles/typography";
import { useState, useEffect } from "react";
import { useTistoryLogin, useTistoryBlogs } from "../useActions";

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: BASE_COLOR,
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  content: {
    padding: "0px 0px 0px",
    textAlign: "center"
  },
  card: {
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    flexDirection: "column",
    backgroundColor: "rgb(240 240 240)",
    padding: "20px 20px 10px"
  },
  item: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "10px",
    flexDirection: "column"
  },
  itemHeader: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "5px 10px",
    minWidth: "450px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  content2: { width: "100%" },
  wrapperInner: {
    display: "flex",
    alignItems: "flex-start",
    flexDirection: "column",
    padding: "0px 15px 15px",
    borderTop: "1px solid lightgray"
  },
  button: {
    margin: "5px"
  },
  appName: {
    color: "white",
    textShadow:
      "-1px -1px 0 blue, 1px -1px 0 blue, -1px 1px 0 blue, 1px 1px 0 blue"
  },
  platformName: {
    fontWeight: "bold"
  }
}));
// b2ef83a76d449d769f337eccd6afd2d8_53799dbf4a5e7fa29210ffbc3e3ca788
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  const [naverChecked, setNaverChecked] = useState(false);
  const [tistoryChecked, setTistoryChecked] = useState(false);
  const { absolve, access_token } = useTistoryLogin();
  const { data } = useTistoryBlogs(access_token);
  const [blogs, setBlogs] = useState([]);
  const [email, setEmail] = useState();
  const [userId, setUserId] = useState();
  useEffect(() => {
    if (access_token && access_token !== "undefined") {
      setTistoryChecked(true);
    }
  }, [access_token]);
  useEffect(() => {
    if (data && data.tistroy) {
      setBlogs(data.tistroy.item.blogs);
      setEmail(data.tistroy.item.id);
      setUserId(data.tistroy.item.userId);
    }
  }, [data]);
  const MyPage = (
    <div className={classes.root}>
      <div className={classes.content}>
        <h1 className={classes.appName}>블로그 셋팅</h1>
        <Card className={classes.card}>
          <Card className={classes.item}>
            <CardActions className={classes.itemHeader}>
              <span className={classes.platformName}>네이버</span>
              <Button
                endIcon={naverChecked ? <ArrowDown /> : <ArrowUp />}
                onClick={() => {
                  // setNaverChecked(!naverChecked);
                }}
              >
                등록
              </Button>
            </CardActions>
            <Collapse
              className={classes.content2}
              classes={{ wrapperInner: classes.wrapperInner }}
              in={naverChecked}
            >
              <TextField fullWidth label="access_token" />
              <TextField fullWidth label="access_token" />
              <TextField fullWidth label="access_token" />
            </Collapse>
          </Card>
          <Card className={classes.item}>
            <CardActions className={classes.itemHeader}>
              <span className={classes.platformName}>티스토리</span>
              <Button
                endIcon={tistoryChecked ? <ArrowDown /> : <ArrowUp />}
                onClick={() => {
                  absolve().then(res => {
                    if (access_token && access_token !== "undefined") {
                      setTistoryChecked(!tistoryChecked);
                    } else {
                      setTistoryChecked(!tistoryChecked);
                      document.write(res.data);
                    }
                  });
                }}
              >
                등록
              </Button>
            </CardActions>
            <Collapse
              className={classes.content2}
              classes={{ wrapperInner: classes.wrapperInner }}
              in={tistoryChecked}
            >
              <TextField fullWidth value={access_token} label="access_token" />
              <TextField fullWidth value={email} label="이메일" />
              <TextField fullWidth value={userId} label="사용자 ID" />
              {blogs.map((blog, index) => {
                return <div key={"blog-" + index}>{blog}</div>;
              })}
            </Collapse>
          </Card>
        </Card>
        <div className={classes.actions}>
          <Button
            className={classes.button}
            variant="contained"
            color="secondary"
            onClick={() => {
              const result = window.confirm(
                "변경된 내용을 저장하지 않고 취소하겠습니까?"
              );
              if (result) route.push("/login");
            }}
          >
            취소
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            onClick={() => {
              const result = window.confirm("변경된 내용을 저장하겠습니까?");
              if (result) route.push("/login");
            }}
          >
            저장
          </Button>
        </div>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

export default Index;
