import React, { useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import { Divider, Tab, Tabs, AppBar } from "@material-ui/core";
import { connect } from "react-redux";

import styled from "styled-components";
import { pTr, BASE_COLOR } from "../../styles/typography";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  padding: {
    padding: pTr(20)
  },
  box: {
    backgroundColor: "rgb(246 246 246)"
  },
  errorRoot: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  }
}));
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
};
function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`
  };
}
function Tabss({
  boxColor = "",
  errMsg = "",
  titles = [""],
  contents = [{}],
  selected_tab = ""
}) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const handleChange = useCallback((event, newValue) => {
    setValue(newValue);
  }, []);

  let contentCountMissMatch = false;

  if (contentCountMissMatch) {
    return <div className={classes.errorRoot}></div>;
  }

  return (
    <div className={classes.root}>
      <div className={classes.box}>
        <Divider />
        <div>
          {/* <AntTabs
            value={value}
            onChange={handleChange}
            // textColor={BASE_COLOR}
            aria-label="ant"
            variant="fullWidth"
          >
            {titles.map((title, index) => (
              <AntTab key={title + index} label={title} />
            ))}
          </AntTabs> */}
          <AppBar position="static">
            <Tabs
              value={value}
              onChange={handleChange}
              aria-label="simple tabs example"
            >
              {titles.map((title, index) => (
                <Tab key={title + index} label={title} {...a11yProps(index)} />
              ))}
            </Tabs>
          </AppBar>
          {contents.map((content, index) => (
            <TabPanel
              key={titles[index] + "-" + index}
              value={value}
              index={0}
              boxColor={boxColor}
            >
              {content}
            </TabPanel>
          ))}
        </div>
      </div>
    </div>
  );
}
function mapStateToProps(state) {
  return {
    selected_tab:
      state.searchinfo.selected_tab == undefined
        ? "doctor"
        : state.searchinfo.selected_tab
  };
}

export default connect(mapStateToProps)(Tabss);
