import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import axios from "axios";
// import { useDispatch } from "react-redux";
import { BASE_URL, API_TIME_OUT } from "../contants/constants";

function useTistoryLogin() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState();
  //   const dispatch = useDispatch();

  const absolve = () => {
    setLoading(true);
    const client_id = process.env.TISTORY_APP_ID;
    const redirect_uri = document.location.href;
    return axios
      .get("https://www.tistory.com/oauth/authorize", {
        params: { client_id, redirect_uri, response_type: "code" }
      })
      .then(tres => {
        res.send(tres.data);
      })
      .catch(err => {
        res.json(err);
      });
  };

  return { absolve, loading, error, result: data };
}

useTistoryLogin.propTypes = {};

export default useTistoryLogin;
