import { useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { getPostCode } from "../reducers/postsinfo";
import { useRouter } from "next/router";
import { TextField, FormControl, Card, Button } from "@material-ui/core";
import { BASE_COLOR } from "../styles/typography";

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: BASE_COLOR,
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  content: {
    padding: "0px 0px 0px",
    textAlign: "center"
  },
  card: {
    backgroundColor: "rgb(240 240 240)",
    padding: "5px 30px 20px"
  },
  textField: {
    margin: "0px 0px 10px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  button: {
    margin: "5px"
  },
  appName: {
    color: "white",
    textShadow:
      "-1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000 "
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  console.log(props.post);
  useEffect(() => {
    props.getPostCode(route.query.code);
  }, []);
  const MyPage = (
    <div className={classes.root}>
      <div className={classes.content}>
        <h1 className={classes.appName}>BlogShot</h1>
        <Card className={classes.card}>
          <FormControl fullWidth variant="outlined">
            <TextField
              className={classes.textField}
              fullWidth
              label={"로그인"}
              defaultValue={props.post.title}
            />
            <TextField
              id="standard-password-input"
              className={classes.textField}
              type="password"
              label={"비밀번호"}
              defaultValue={props.post.context}
            />
          </FormControl>
        </Card>
        <div className={classes.actions}>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
          >
            로그인
          </Button>
          <Button
            className={classes.button}
            variant="contained"
            color="default"
          >
            블로그 셋팅
          </Button>
        </div>
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

const mapStateToProps = state => {
  return { post: state.postsinfo.selectedPost };
};
const mapDispatchToProps = dispatch =>
  bindActionCreators({ getPostCode }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Index);
