import { makeStyles } from "@material-ui/styles";

import Layout from "../components/layouts/Layout";

import ActionMyBar from "../components/component/ActionMyBar";

import { useRouter } from "next/router";
import {
  TextField,
  FormControl,
  Card,
  Button,
  CircularProgress,
  InputLabel,
  Select,
  MenuItem
} from "@material-ui/core";
import useDataOne from "../useActions/useDataOne";
import usePost from "../useActions/usePost";
import { useState, useEffect } from "react";

const useStyles = makeStyles(theme => ({
  root: {},
  content: {
    padding: "64px 0px 0px"
  },
  card: {
    backgroundColor: "rgb(240 240 240)",
    padding: "20px",
    margin: "0px 20px 20px"
  },
  textField: {
    margin: "10px 10px 20px"
  },
  actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "0px 20px 5px",
    marginTop: "15px"
  },
  button: {
    margin: "5px"
  },
  process: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  formControl: {
    display: "flex",
    flexDirection: "row"
  },
  select: {
    marginLeft: "10px"
  }
}));
const Index = props => {
  const classes = useStyles();
  const route = useRouter();
  const { data: post, loading, error } = useDataOne("posts", route.query.code);
  const [updatePost, setUpdatePost] = useState(post);
  const { postSave, postDelete, posting } = usePost();

  const [categoryData, setCategoryData] = useState([]);
  const [selected, setSelected] = useState();

  useEffect(() => {
    let category = localStorage.getItem("category");
    let category_sel = localStorage.getItem("category_sel");
    if (category) {
      category = JSON.parse(category);
      setCategoryData(category);
    }
    if (category_sel) {
      category_sel = JSON.parse(category_sel);
      setSelected(category_sel);
    }
  }, []);
  useEffect(() => {
    setUpdatePost(post);
  }, [post]);

  const handleOnSave = () => {
    postSave(
      post.code,
      {
        title: updatePost.title,
        context: updatePost.context
      },
      {
        success: res => {
          if (res.data && res.data.msg) {
            alert(res.data.msg);
          }
        },
        fail: err => {
          if (err.response.data && err.response.data.msg) {
            alert(err.response.data.msg);
          }
        }
      }
    );
  };
  const handleOnDelete = () => {
    if (window.confirm("정말 이 포스트를 삭제하시겠습니까?"))
      postDelete(post.code, {
        success: res => {
          if (res.data && res.data.msg) {
            alert(res.data.msg);
            route.back();
          }
        },
        fail: err => {
          if (err.response.data && err.response.data.msg) {
            alert(err.response.data.msg);
          }
        }
      });
  };
  const handleOnPosting = () => {
    posting(updatePost, {
      success: sres => {
        if (sres.data && sres.data.msg) {
          alert(sres.data.msg);
          route.back();
        }
      },
      fail: err => {
        if (err.response && err.response.data) alert(err.response.data.msg);
        console.log(err);
      }
    });
  };
  const handleUpdataPost = key => e => {
    setUpdatePost({ ...updatePost, [key]: e.target.value });
  };
  const handleChange = e => setSelected(e.target.value);

  const handleKeyDown = e => {
    if (e.key === "Enter") {
      const datas = [...categoryData, e.target.value];
      localStorage.setItem("category", JSON.stringify(datas));
      setCategoryData(datas);
      localStorage.setItem("category_sel", JSON.stringify(e.target.value));
    }
  };

  const MyPage = (
    <div className={classes.root}>
      <ActionMyBar homeEnable={false} title={post.code || "포스트"} />
      <div className={classes.content}>
        <div className={classes.actions}>
          <FormControl variant="filled" classes={{ root: classes.formControl }}>
            <TextField label="카테고리" onKeyDown={handleKeyDown} />
            <Select
              className={classes.select}
              labelId="demo-simple-select-filled-label"
              id="demo-simple-select-filled"
              value={selected}
              onChange={handleChange}
            >
              {categoryData.map((item, index) => {
                return (
                  <MenuItem key={item + "-" + index} value={item}>
                    {item}
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
          <span>
            <Button
              className={classes.button}
              variant="contained"
              color="secondary"
              disabled={loading}
              onClick={handleOnDelete}
            >
              삭제
            </Button>
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              disabled={loading}
              onClick={handleOnSave}
            >
              저장
            </Button>
            <Button
              className={classes.button}
              variant="contained"
              color="default"
              disabled={loading}
              onClick={handleOnPosting}
            >
              포스팅
            </Button>
          </span>
        </div>

        <Card className={classes.card}>
          {loading ? (
            <div className={classes.process}>
              <CircularProgress />
            </div>
          ) : (
            <FormControl fullWidth variant="outlined">
              <TextField
                className={classes.textField}
                multiline
                fullWidth
                label={"제목"}
                onChange={handleUpdataPost("title")}
                defaultValue={post.title}
              />
              <TextField
                className={classes.textField}
                multiline
                fullWidth
                label={"내용"}
                onChange={handleUpdataPost("context")}
                defaultValue={post.context}
              />
            </FormControl>
          )}
        </Card>
        {error && <p>{error}</p>}
      </div>
    </div>
  );

  return <Layout cookies={props.cookies} content={MyPage} />;
};

export default Index;
